'use client'

import { useState, useEffect } from 'react'
import { useStation } from '@/contexts/StationContext'
import { FormCard } from '@/components/ui/FormCard'
import { DataTable } from '@/components/ui/DataTable'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog'
import { Badge } from '@/components/ui/badge'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { MoneyInput } from '@/components/inputs/MoneyInput'
import { Briefcase, Plus, Edit, Trash2, Phone, Mail, Building2, DollarSign, ArrowLeft } from 'lucide-react'
import { useToast } from '@/hooks/use-toast'
import { useRouter } from 'next/navigation'

interface OfficeStaff {
  id: string
  name: string
  employeeId: string | null
  stationId: string
  role: 'MANAGER' | 'SUPERVISOR' | 'OFFICE_STAFF' | 'ACCOUNTANT' | 'CASHIER'
  phone: string | null
  email: string | null
  baseSalary: number
  specialAllowance?: number
  otherAllowances?: number
  medicalAllowance?: number
  holidayAllowance?: number
  fuelAllowance?: number
  hireDate: string | null
  isActive: boolean
  createdAt: string
  updatedAt: string
  station?: {
    id: string
    name: string
  }
}

export default function OfficeStaffPage() {
  const router = useRouter()
  const { selectedStation, stations } = useStation()
  const [officeStaff, setOfficeStaff] = useState<OfficeStaff[]>([])
  const [loading, setLoading] = useState(true)
  const [dialogOpen, setDialogOpen] = useState(false)
  const [editingStaff, setEditingStaff] = useState<OfficeStaff | null>(null)
  const [deletingStaffId, setDeletingStaffId] = useState<string | null>(null)
  const [formData, setFormData] = useState({
    name: '',
    employeeId: '',
    stationId: selectedStation || '',
    role: 'OFFICE_STAFF' as OfficeStaff['role'],
    phone: '',
    email: '',
    baseSalary: 0,
    specialAllowance: 0,
    otherAllowances: 0,
    medicalAllowance: 0,
    holidayAllowance: 0,
    fuelAllowance: 0,
    hireDate: new Date().toISOString().split('T')[0],
    isActive: true
  })
  const { toast } = useToast()

  useEffect(() => {
    if (selectedStation) {
      fetchOfficeStaff()
      // Update formData stationId when selectedStation changes
      setFormData(prev => ({
        ...prev,
        stationId: selectedStation
      }))
    }
  }, [selectedStation])

  const fetchOfficeStaff = async () => {
    if (!selectedStation) return

    try {
      setLoading(true)
      const response = await fetch(`/api/office-staff?stationId=${selectedStation}`)
      if (!response.ok) throw new Error('Failed to fetch office staff')
      const data = await response.json()
      setOfficeStaff(data)
    } catch (_error) {
      toast({
        title: "Error",
        description: "Failed to fetch office staff",
        variant: "destructive"
      })
    } finally {
      setLoading(false)
    }
  }

  const [isSubmitting, setIsSubmitting] = useState(false)

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()

    if (isSubmitting) return

    // Ensure stationId is set from selectedStation
    if (!selectedStation) {
      toast({
        title: "Error",
        description: "Please select a station first",
        variant: "destructive"
      })
      return
    }

    setIsSubmitting(true)

    try {
      const url = editingStaff ? `/api/office-staff/${editingStaff.id}` : '/api/office-staff'
      const method = editingStaff ? 'PUT' : 'POST'

      // Always use current selectedStation for stationId
      const submitData = {
        ...formData,
        stationId: selectedStation, // Always use current selectedStation
        ...(editingStaff ? {} : { employeeId: undefined }) // Remove employeeId when creating (auto-generated)
      }

      console.log('ðŸ“¤ Submitting office staff data:', submitData)

      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(submitData)
      })

      console.log('ðŸ“¥ Response status:', response.status, response.statusText)

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}))
        const errorMessage = errorData.details || errorData.error || `Failed to ${editingStaff ? 'update' : 'create'} office staff`
        throw new Error(errorMessage)
      }

      await fetchOfficeStaff()
      handleCloseDialog()

      toast({
        title: "Success",
        description: `Office staff ${editingStaff ? 'updated' : 'created'} successfully`
      })
    } catch (error) {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : `Failed to ${editingStaff ? 'update' : 'create'} office staff`,
        variant: "destructive"
      })
    } finally {
      setIsSubmitting(false)
    }
  }

  const handleDelete = async (id: string) => {
    if (!confirm('Are you sure you want to delete this office staff member?')) return

    try {
      const response = await fetch(`/api/office-staff/${id}`, {
        method: 'DELETE'
      })

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}))
        throw new Error(errorData.error || 'Failed to delete office staff')
      }

      await fetchOfficeStaff()
      setDeletingStaffId(null)

      toast({
        title: "Success",
        description: "Office staff deleted successfully"
      })
    } catch (error) {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to delete office staff",
        variant: "destructive"
      })
    }
  }

  const handleEdit = (staff: OfficeStaff) => {
    setEditingStaff(staff)
    setFormData({
      name: staff.name || '',
      employeeId: staff.employeeId || '',
      stationId: staff.stationId || '',
      role: staff.role,
      phone: staff.phone || '',
      email: staff.email || '',
      baseSalary: staff.baseSalary || 0,
      specialAllowance: staff.specialAllowance || 0,
      otherAllowances: staff.otherAllowances || 0,
      medicalAllowance: staff.medicalAllowance || 0,
      holidayAllowance: staff.holidayAllowance || 0,
      fuelAllowance: staff.fuelAllowance || 0,
      hireDate: staff.hireDate ? new Date(staff.hireDate).toISOString().split('T')[0] : '',
      isActive: staff.isActive
    })
    setDialogOpen(true)
  }

  const handleCloseDialog = () => {
    setDialogOpen(false)
    setEditingStaff(null)
    setFormData({
      name: '',
      employeeId: '',
      stationId: selectedStation || '',
      role: 'OFFICE_STAFF',
      phone: '',
      email: '',
      baseSalary: 0,
      specialAllowance: 0,
      otherAllowances: 0,
      medicalAllowance: 0,
      holidayAllowance: 0,
      fuelAllowance: 0,
      hireDate: new Date().toISOString().split('T')[0],
      isActive: true
    })
  }

  const columns = [
    {
      key: 'name' as keyof OfficeStaff,
      title: 'Name',
      render: (value: unknown, row: OfficeStaff) => (
        <div className="flex items-center gap-2">
          <Briefcase className="h-4 w-4 text-muted-foreground" />
          <div>
            <div className="font-medium">{value as string}</div>
            {row.employeeId && (
              <div className="text-xs text-muted-foreground">ID: {row.employeeId}</div>
            )}
          </div>
        </div>
      )
    },
    {
      key: 'role' as keyof OfficeStaff,
      title: 'Role',
      render: (value: unknown) => (
        <Badge variant="outline">{String(value).replace('_', ' ')}</Badge>
      )
    },
    {
      key: 'phone' as keyof OfficeStaff,
      title: 'Phone',
      render: (value: unknown) => value ? (
        <div className="flex items-center gap-1">
          <Phone className="h-3 w-3 text-muted-foreground" />
          <span>{value as string}</span>
        </div>
      ) : (
        <span className="text-muted-foreground">-</span>
      )
    },
    {
      key: 'email' as keyof OfficeStaff,
      title: 'Email',
      render: (value: unknown) => value ? (
        <div className="flex items-center gap-1">
          <Mail className="h-3 w-3 text-muted-foreground" />
          <span>{value as string}</span>
        </div>
      ) : (
        <span className="text-muted-foreground">-</span>
      )
    },
    {
      key: 'baseSalary' as keyof OfficeStaff,
      title: 'Base Salary',
      render: (value: unknown) => (
        <div className="flex items-center gap-1">
          <DollarSign className="h-4 w-4 text-green-600" />
          <span>Rs. {(value as number).toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</span>
        </div>
      )
    },
    {
      key: 'isActive' as keyof OfficeStaff,
      title: 'Status',
      render: (value: unknown) => (
        <Badge variant={value ? 'default' : 'secondary'}>
          {value ? 'Active' : 'Inactive'}
        </Badge>
      )
    },
    {
      key: 'id' as keyof OfficeStaff,
      title: 'Actions',
      render: (value: unknown, row: OfficeStaff) => (
        <div className="flex items-center gap-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => handleEdit(row)}
          >
            <Edit className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => handleDelete(row.id)}
            disabled={deletingStaffId === row.id}
            className="text-red-600 dark:text-red-400 hover:text-red-700 disabled:opacity-50"
          >
            {deletingStaffId === row.id ? (
              <span className="text-xs">Deleting...</span>
            ) : (
              <Trash2 className="h-4 w-4" />
            )}
          </Button>
        </div>
      )
    }
  ]

  if (!selectedStation) {
    return (
      <div className="space-y-6 p-6">
        <FormCard title="Office Staff Management" description="Manage office staff members">
          <div className="text-center py-8 text-muted-foreground">
            <Building2 className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
            <p>Please select a station to manage office staff</p>
          </div>
        </FormCard>
      </div>
    )
  }

  return (
    <div className="space-y-6 p-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          <Button variant="outline" onClick={() => router.push('/settings')}>
            <ArrowLeft className="mr-2 h-4 w-4" />
            Back
          </Button>
          <div>
            <h1 className="text-3xl font-bold text-foreground flex items-center gap-2">
              <Briefcase className="h-8 w-8 text-orange-600 dark:text-orange-400" />
              Office Staff Management
            </h1>
            <p className="text-muted-foreground mt-1">
              Manage office employees (Managers, Supervisors, Office Staff, Accountants, Cashiers)
            </p>
          </div>
        </div>
        <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={handleCloseDialog}>
              <Plus className="mr-2 h-4 w-4" />
              Add Office Staff
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>{editingStaff ? 'Edit Office Staff' : 'Add New Office Staff'}</DialogTitle>
              <DialogDescription>
                {editingStaff ? 'Update office staff information' : 'Enter details for the new office staff member'}
              </DialogDescription>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="name">Name *</Label>
                  <Input
                    id="name"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    placeholder="John Doe"
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="employeeId">Employee ID</Label>
                  <Input
                    id="employeeId"
                    value={formData.employeeId}
                    onChange={(e) => setFormData({ ...formData, employeeId: e.target.value })}
                    placeholder={editingStaff ? formData.employeeId : "Auto-generated (OFC001)"}
                    disabled={!editingStaff}
                  />
                  {!editingStaff && (
                    <p className="text-xs text-muted-foreground mt-1">Auto-generated on creation</p>
                  )}
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="role">Role *</Label>
                  <Select
                    value={formData.role}
                    onValueChange={(value) => {
                      const newRole = value as OfficeStaff['role']
                      // Reset fuel allowance if role changes from MANAGER
                      setFormData({
                        ...formData,
                        role: newRole,
                        fuelAllowance: newRole === 'MANAGER' ? formData.fuelAllowance : 0
                      })
                    }}
                  >
                    <SelectTrigger id="role">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="MANAGER">Manager</SelectItem>
                      <SelectItem value="SUPERVISOR">Supervisor</SelectItem>
                      <SelectItem value="OFFICE_STAFF">Office Staff</SelectItem>
                      <SelectItem value="ACCOUNTANT">Accountant</SelectItem>
                      <SelectItem value="CASHIER">Cashier</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label htmlFor="baseSalary">Base Salary (Rs.) *</Label>
                  <MoneyInput
                    value={formData.baseSalary}
                    onChange={(value) => setFormData({ ...formData, baseSalary: value || 0 })}
                    placeholder="0.00"
                  />
                </div>
              </div>

              {/* Allowances Section */}
              <div className="space-y-3 pt-2 border-t">
                <h4 className="text-sm font-semibold">Allowances (Rs.)</h4>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="specialAllowance">Special Allowance</Label>
                    <MoneyInput
                      value={formData.specialAllowance}
                      onChange={(value) => setFormData({ ...formData, specialAllowance: value || 0 })}
                      placeholder="0.00"
                    />
                    <p className="text-xs text-muted-foreground mt-1">Different for each staff member</p>
                  </div>
                  <div>
                    <Label htmlFor="otherAllowances">Other Allowances</Label>
                    <MoneyInput
                      value={formData.otherAllowances}
                      onChange={(value) => setFormData({ ...formData, otherAllowances: value || 0 })}
                      placeholder="0.00"
                    />
                  </div>
                  <div>
                    <Label htmlFor="medicalAllowance">Medical Allowance</Label>
                    <MoneyInput
                      value={formData.medicalAllowance}
                      onChange={(value) => setFormData({ ...formData, medicalAllowance: value || 0 })}
                      placeholder="0.00"
                    />
                  </div>
                  <div>
                    <Label htmlFor="holidayAllowance">Holiday Allowance</Label>
                    <MoneyInput
                      value={formData.holidayAllowance}
                      onChange={(value) => setFormData({ ...formData, holidayAllowance: value || 0 })}
                      placeholder="0.00"
                    />
                  </div>
                  <div>
                    <Label htmlFor="fuelAllowance">Fuel Allowance</Label>
                    <MoneyInput
                      value={formData.fuelAllowance}
                      onChange={(value) => setFormData({ ...formData, fuelAllowance: value || 0 })}
                      placeholder="0.00"
                      disabled={formData.role !== 'MANAGER'}
                    />
                    {formData.role === 'MANAGER' ? (
                      <p className="text-xs text-muted-foreground mt-1">Only for managers</p>
                    ) : (
                      <p className="text-xs text-muted-foreground mt-1 text-orange-600">Only available for Manager role</p>
                    )}
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="phone">Phone</Label>
                  <Input
                    id="phone"
                    value={formData.phone}
                    onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                    placeholder="0712345678"
                    type="tel"
                  />
                </div>
                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    value={formData.email}
                    onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                    placeholder="john@example.com"
                    type="email"
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="hireDate">Hire Date</Label>
                  <Input
                    id="hireDate"
                    value={formData.hireDate}
                    onChange={(e) => setFormData({ ...formData, hireDate: e.target.value })}
                    type="date"
                  />
                </div>
                <div>
                  <Label htmlFor="isActive">Status</Label>
                  <Select
                    value={formData.isActive ? 'true' : 'false'}
                    onValueChange={(value) => setFormData({ ...formData, isActive: value === 'true' })}
                  >
                    <SelectTrigger id="isActive">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="true">Active</SelectItem>
                      <SelectItem value="false">Inactive</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="flex justify-end gap-2 pt-4">
                <Button type="button" variant="outline" onClick={handleCloseDialog} disabled={isSubmitting}>
                  Cancel
                </Button>
                <Button type="submit" disabled={isSubmitting}>
                  {isSubmitting ? 'Saving...' : editingStaff ? 'Update' : 'Create'}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <FormCard
        title="Office Staff List"
        description={`Office staff members for ${stations.find(s => s.id === selectedStation)?.name || 'selected station'}`}
      >
        {loading ? (
          <div className="flex items-center justify-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-orange-600 dark:border-orange-400"></div>
          </div>
        ) : officeStaff.length === 0 ? (
          <div className="text-center py-8 text-muted-foreground">
            <Briefcase className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
            <p>No office staff found</p>
            <p className="text-sm">Click &quot;Add Office Staff&quot; to create a new office staff member</p>
          </div>
        ) : (
          <DataTable
            data={officeStaff}
            columns={columns}
            searchPlaceholder="Search office staff..."
          />
        )}
      </FormCard>
    </div>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { useStation } from '@/contexts/StationContext'
import { FormCard } from '@/components/ui/FormCard'
import { DataTable, Column } from '@/components/ui/DataTable'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription, DialogTrigger } from '@/components/ui/dialog'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { DollarSign, Plus, TrendingUp, Calendar, AlertCircle, Fuel, BarChart3, Droplet, Zap, Package } from 'lucide-react'
import { useToast } from '@/hooks/use-toast'
import { MoneyInput } from '@/components/inputs/MoneyInput'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'
import { useRouter } from 'next/navigation'
import { ArrowLeft } from 'lucide-react'

interface Fuel {
  id: string
  code: string
  name: string
  category: string
  description?: string | null
  icon?: string | null
  isActive: boolean
  sortOrder: number
  _count?: {
    tanks: number
    prices: number
  }
}

interface Price {
  id: string
  stationId: string
  fuelId: string
  fuel?: Fuel
  price: number
  effectiveDate: string
  isActive: boolean
  createdAt: string
  station?: {
    name: string
  }
}

interface Tank {
  id: string
  tankNumber: string
  fuelId: string
  fuel?: Fuel
  capacity: number
  currentLevel: number
  station: {
    name: string
  }
}

export default function FuelsPage() {
  const router = useRouter()
  const [prices, setPrices] = useState<Price[]>([])
  const [tanks, setTanks] = useState<Tank[]>([])
  const [fuels, setFuels] = useState<Fuel[]>([])
  const [loading, setLoading] = useState(true)
  const [priceDialogOpen, setPriceDialogOpen] = useState(false)
  const [fuelTypeDialogOpen, setFuelTypeDialogOpen] = useState(false)
  const [newFuelDialogOpen, setNewFuelDialogOpen] = useState(false)
  const [activeTab, setActiveTab] = useState('overview')
  const [formData, setFormData] = useState({
    fuelId: '',
    price: 0,
    effectiveDate: new Date().toISOString().slice(0, 16)
  })
  const [newFuelData, setNewFuelData] = useState({
    code: '',
    name: '',
    category: 'PETROL',
    description: '',
    icon: 'â›½'
  })
  const { selectedStation } = useStation()
  const { toast } = useToast()

  useEffect(() => {
    fetchFuels()
    fetchPrices()
    fetchTanks()
  }, [selectedStation])

  const fetchFuels = async () => {
    try {
      const response = await fetch('/api/fuels')
      if (!response.ok) throw new Error('Failed to fetch fuels')

      const data = await response.json()
      setFuels(Array.isArray(data) ? data : [])

      // Set default fuel if not set
      if (data.length > 0 && !formData.fuelId) {
        setFormData(prev => ({ ...prev, fuelId: data[0].id }))
      }
    } catch (error) {
      console.error('Error fetching fuels:', error)
      toast({
        title: "Error",
        description: "Failed to fetch fuel types",
        variant: "destructive"
      })
    }
  }

  const fetchPrices = async () => {
    setLoading(true)
    try {
      const url = selectedStation && selectedStation !== 'all'
        ? `/api/prices?stationId=${selectedStation}`
        : '/api/prices'

      const response = await fetch(url)
      if (!response.ok) throw new Error('Failed to fetch prices')

      const data = await response.json()
      setPrices(Array.isArray(data) ? data : [])
    } catch (error) {
      console.error('Error fetching prices:', error)
      toast({
        title: "Error",
        description: "Failed to fetch prices",
        variant: "destructive"
      })
      setPrices([])
    } finally {
      setLoading(false)
    }
  }

  const fetchTanks = async () => {
    try {
      const url = selectedStation && selectedStation !== 'all'
        ? `/api/tanks?stationId=${selectedStation}`
        : '/api/tanks'

      const response = await fetch(url)
      if (!response.ok) throw new Error('Failed to fetch tanks')

      const data = await response.json()
      setTanks(Array.isArray(data) ? data : [])
    } catch (error) {
      console.error('Error fetching tanks:', error)
    }
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()

    if (!selectedStation || selectedStation === 'all') {
      toast({
        title: "Error",
        description: "Please select a specific station",
        variant: "destructive"
      })
      return
    }

    if (!formData.fuelId) {
      toast({
        title: "Error",
        description: "Please select a fuel type",
        variant: "destructive"
      })
      return
    }

    if (formData.price <= 0) {
      toast({
        title: "Error",
        description: "Price must be greater than 0",
        variant: "destructive"
      })
      return
    }

    try {
      const response = await fetch('/api/prices', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          stationId: selectedStation,
          fuelId: formData.fuelId,
          price: parseFloat(formData.price.toString()),
          effectiveDate: new Date(formData.effectiveDate).toISOString()
        })
      })

      if (!response.ok) {
        const error = await response.json()
        throw new Error(error.error || 'Failed to add price')
      }

      toast({
        title: "Success",
        description: "New price added successfully"
      })

      setPriceDialogOpen(false)
      setFormData({
        fuelId: fuels[0]?.id || '',
        price: 0,
        effectiveDate: new Date().toISOString().slice(0, 16)
      })
      fetchPrices()
    } catch (error) {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : 'Failed to add price',
        variant: "destructive"
      })
    }
  }

  const handleAddFuel = async (e: React.FormEvent) => {
    e.preventDefault()

    if (!newFuelData.code || !newFuelData.name) {
      toast({
        title: "Error",
        description: "Code and name are required",
        variant: "destructive"
      })
      return
    }

    try {
      const response = await fetch('/api/fuels', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newFuelData)
      })

      if (!response.ok) {
        const error = await response.json()
        throw new Error(error.error || 'Failed to add fuel type')
      }

      const newFuel = await response.json()

      toast({
        title: "Success",
        description: `${newFuelData.name} added successfully!`
      })

      setNewFuelDialogOpen(false)
      setNewFuelData({
        code: '',
        name: '',
        category: 'PETROL',
        description: '',
        icon: 'â›½'
      })

      // Refresh fuels list
      await fetchFuels()

      // Open price dialog with new fuel selected
      setFormData(prev => ({ ...prev, fuelId: newFuel.id }))
      setFuelTypeDialogOpen(false)
      setPriceDialogOpen(true)
    } catch (error) {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : 'Failed to add fuel type',
        variant: "destructive"
      })
    }
  }

  // Get current prices (most recent for each fuel type)
  const getCurrentPrices = () => {
    const currentPricesMap = new Map<string, Price>()

    // Filter out any invalid prices and sort by date
    const validPrices = prices.filter(p => p && p.fuelId && p.effectiveDate && p.price !== undefined)

    const sortedPrices = [...validPrices].sort((a, b) =>
      new Date(b.effectiveDate).getTime() - new Date(a.effectiveDate).getTime()
    )

    // Get the most recent price for each fuel type
    for (const price of sortedPrices) {
      if (!currentPricesMap.has(price.fuelId)) {
        currentPricesMap.set(price.fuelId, price)
      }
    }

    return Array.from(currentPricesMap.values())
  }

  const currentPrices = getCurrentPrices()
  const activePricesCount = currentPrices.filter(p =>
    p && p.effectiveDate && new Date(p.effectiveDate) <= new Date()
  ).length

  // Get valid prices for history table
  const validPricesForHistory = prices.filter(p =>
    p && p.fuelId && p.effectiveDate !== undefined && p.price !== undefined
  )

  // Calculate fuel statistics
  const getFuelStats = () => {
    const stats = new Map<string, {
      totalCapacity: number,
      currentStock: number,
      tankCount: number,
      currentPrice: number
    }>()

    // Initialize stats for all fuel types
    fuels.forEach(fuel => {
      stats.set(fuel.id, {
        totalCapacity: 0,
        currentStock: 0,
        tankCount: 0,
        currentPrice: 0
      })
    })

    // Aggregate tank data
    tanks.forEach(tank => {
      const stat = stats.get(tank.fuelId)
      if (stat) {
        stat.totalCapacity += tank.capacity
        stat.currentStock += tank.currentLevel
        stat.tankCount += 1
      }
    })

    // Add price data
    currentPrices.forEach(price => {
      const stat = stats.get(price.fuelId)
      if (stat) {
        stat.currentPrice = price.price || 0
      }
    })

    return stats
  }

  const fuelStats = getFuelStats()

  // Helper function to determine if a price is the current active one
  const isCurrentPrice = (price: Price): boolean => {
    const now = new Date()
    const priceDate = new Date(price.effectiveDate)

    // If price is in the future, it's not current
    if (priceDate > now) return false

    // Find all prices for the same fuel type and station
    const sameFuelPrices = prices.filter(p =>
      p.fuelId === price.fuelId &&
      p.stationId === price.stationId &&
      new Date(p.effectiveDate) <= now
    )

    // Sort by date descending to find the most recent
    const sortedPrices = sameFuelPrices.sort((a, b) =>
      new Date(b.effectiveDate).getTime() - new Date(a.effectiveDate).getTime()
    )

    // This price is current if it's the most recent one
    return sortedPrices.length > 0 && sortedPrices[0].id === price.id
  }

  const columns: Column<Price>[] = [
    {
      key: 'fuelId',
      title: 'Fuel Type',
      render: (value, row) => (
        <div className="flex items-center gap-2">
          <span className="text-xl">{row.fuel?.icon || 'â›½'}</span>
          <span className="font-medium">{row.fuel?.name || 'Unknown'}</span>
        </div>
      )
    },
    {
      key: 'price',
      title: 'Price (LKR/L)',
      render: (value, row) => (
        <div className="font-semibold text-lg">
          Rs. {row.price.toFixed(2)}
        </div>
      )
    },
    {
      key: 'effectiveDate',
      title: 'Effective From',
      render: (value, row) => (
        <div className="text-sm">
          {new Date(row.effectiveDate).toLocaleString('en-US', {
            year: 'numeric',
            month: 'short',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit'
          })}
        </div>
      )
    },
    {
      key: 'stationId',
      title: 'Station',
      render: (value, row) => (
        <div className="text-sm text-muted-foreground">
          {row.station?.name || 'Unknown'}
        </div>
      )
    },
    {
      key: 'isActive',
      title: 'Status',
      render: (value, row) => {
        const priceDate = new Date(row.effectiveDate)
        const now = new Date()

        // Future price
        if (priceDate > now) {
          return (
            <Badge variant="secondary" className="bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-300">
              Scheduled
            </Badge>
          )
        }

        // Check if this is the current active price
        const isCurrent = isCurrentPrice(row)

        if (isCurrent) {
          return (
            <Badge variant="default" className="bg-green-600 text-white">
              Active
            </Badge>
          )
        }

        // Historical/superseded price
        return (
          <Badge variant="secondary" className="bg-gray-200 text-gray-700 dark:bg-gray-700 dark:text-gray-300">
            Historical
          </Badge>
        )
      }
    }
  ]


  const totalCapacity = Array.from(fuelStats.values()).reduce((sum, stat) => sum + stat.totalCapacity, 0)
  const totalStock = Array.from(fuelStats.values()).reduce((sum, stat) => sum + stat.currentStock, 0)
  const stockPercentage = totalCapacity > 0 ? (totalStock / totalCapacity) * 100 : 0

  return (
    <div className="space-y-6 p-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div className="flex items-center gap-4">
          <Button variant="outline" onClick={() => router.push('/settings')}>
            <ArrowLeft className="mr-2 h-4 w-4" />
            Back
          </Button>
          <div>
            <h1 className="text-3xl font-bold tracking-tight">Fuels</h1>
            <p className="text-muted-foreground mt-1">
              Comprehensive fuel management - types, pricing, and inventory
            </p>
          </div>
        </div>
        <div className="flex gap-2">
          <Dialog open={fuelTypeDialogOpen} onOpenChange={setFuelTypeDialogOpen}>
            <DialogTrigger asChild>
              <Button variant="outline">
                <Plus className="mr-2 h-4 w-4" />
                Add New Fuel Type
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-3xl max-h-[85vh] overflow-hidden flex flex-col">
              <DialogHeader>
                <DialogTitle>Add New Fuel Type</DialogTitle>
                <DialogDescription>
                  Add a completely new fuel or oil type to your system.
                </DialogDescription>
              </DialogHeader>

              <div className="overflow-y-auto flex-1 pr-2">
                <form onSubmit={handleAddFuel} className="space-y-4 mt-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="newFuelName">Fuel Name *</Label>
                      <Input
                        id="newFuelName"
                        value={newFuelData.name}
                        onChange={(e) => setNewFuelData({ ...newFuelData, name: e.target.value })}
                        placeholder="e.g., Bio Diesel, Hydrogen"
                        required
                      />
                    </div>

                    <div>
                      <Label htmlFor="newFuelCode">Code *</Label>
                      <Input
                        id="newFuelCode"
                        value={newFuelData.code}
                        onChange={(e) => setNewFuelData({ ...newFuelData, code: e.target.value.toUpperCase().replace(/\s+/g, '_') })}
                        placeholder="e.g., BIO_DIESEL"
                        required
                      />
                      <p className="text-xs text-muted-foreground mt-1">Unique identifier (auto-formatted)</p>
                    </div>
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="newFuelCategory">Category *</Label>
                      <Select
                        value={newFuelData.category}
                        onValueChange={(value) => setNewFuelData({ ...newFuelData, category: value })}
                      >
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="PETROL">Petrol</SelectItem>
                          <SelectItem value="DIESEL">Diesel</SelectItem>
                          <SelectItem value="OIL">Oil</SelectItem>
                          <SelectItem value="GAS">Gas (LPG/CNG)</SelectItem>
                          <SelectItem value="ELECTRIC">Electric</SelectItem>
                          <SelectItem value="HYDROGEN">Hydrogen</SelectItem>
                          <SelectItem value="OTHER">Other</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>

                    <div>
                      <Label htmlFor="newFuelIcon">Icon (Emoji)</Label>
                      <Input
                        id="newFuelIcon"
                        value={newFuelData.icon}
                        onChange={(e) => setNewFuelData({ ...newFuelData, icon: e.target.value })}
                        placeholder="â›½"
                        maxLength={2}
                      />
                      <p className="text-xs text-muted-foreground mt-1">Single emoji character</p>
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="newFuelDescription">Description (Optional)</Label>
                    <Input
                      id="newFuelDescription"
                      value={newFuelData.description}
                      onChange={(e) => setNewFuelData({ ...newFuelData, description: e.target.value })}
                      placeholder="e.g., Environmentally friendly bio-diesel blend"
                    />
                  </div>

                  <div className="bg-orange-50 dark:bg-orange-950/30 p-4 rounded-lg">
                    <h4 className="font-semibold text-sm mb-2 flex items-center gap-2">
                      <AlertCircle className="h-4 w-4 text-orange-600" />
                      Preview
                    </h4>
                    <div className="flex items-center gap-3 bg-white dark:bg-gray-900 p-3 rounded border">
                      <span className="text-2xl">{newFuelData.icon || 'â›½'}</span>
                      <div>
                        <p className="font-medium">{newFuelData.name || 'Fuel Name'}</p>
                        <p className="text-xs text-muted-foreground">{newFuelData.code || 'FUEL_CODE'} â€¢ {newFuelData.category}</p>
                        {newFuelData.description && (
                          <p className="text-xs text-muted-foreground mt-1">{newFuelData.description}</p>
                        )}
                      </div>
                    </div>
                  </div>

                  <div className="flex justify-end gap-2 pt-4 border-t">
                    <Button type="button" variant="outline" onClick={() => setFuelTypeDialogOpen(false)}>
                      Cancel
                    </Button>
                    <Button type="submit">
                      <Plus className="mr-2 h-4 w-4" />
                      Add Fuel Type
                    </Button>
                  </div>
                </form>

                <div className="mt-6 pt-6 border-t">
                  <h4 className="font-semibold text-sm mb-3">Existing Fuel Types ({fuels.length})</h4>
                  <div className="grid grid-cols-2 gap-2 max-h-40 overflow-y-auto">
                    {fuels.map((fuel) => (
                      <div
                        key={fuel.id}
                        className="flex items-center gap-2 p-2 rounded border bg-gray-50 dark:bg-gray-900"
                      >
                        <span className="text-lg">{fuel.icon}</span>
                        <span className="flex-1 text-xs font-medium">{fuel.name}</span>
                        {fuel.isActive && <Badge variant="default" className="text-xs">Active</Badge>}
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </DialogContent>
          </Dialog>

          <Dialog open={priceDialogOpen} onOpenChange={setPriceDialogOpen}>
            <DialogTrigger asChild>
              <Button>
                <Plus className="mr-2 h-4 w-4" />
                Add New Price
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Add New Fuel Price</DialogTitle>
                <DialogDescription>
                  Add a new price for a fuel type. This will be effective from the date you specify.
                </DialogDescription>
              </DialogHeader>
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <Label htmlFor="fuelId">Fuel Type</Label>
                  <Select
                    value={formData.fuelId}
                    onValueChange={(value) => setFormData({ ...formData, fuelId: value })}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select fuel type" />
                    </SelectTrigger>
                    <SelectContent>
                      {fuels.filter(f => f.isActive).map(fuel => (
                        <SelectItem key={fuel.id} value={fuel.id}>
                          {fuel.icon} {fuel.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="price">Price (LKR per Liter)</Label>
                  <MoneyInput
                    id="price"
                    value={formData.price}
                    onChange={(value) => setFormData({ ...formData, price: value })}
                    placeholder="0.00"
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="effectiveDate">Effective From</Label>
                  <Input
                    type="datetime-local"
                    id="effectiveDate"
                    value={formData.effectiveDate}
                    onChange={(e) => setFormData({ ...formData, effectiveDate: e.target.value })}
                    required
                  />
                </div>

                {selectedStation === 'all' && (
                  <div className="bg-yellow-50 dark:bg-yellow-950 p-3 rounded-lg flex items-start gap-2">
                    <AlertCircle className="h-5 w-5 text-yellow-600 dark:text-yellow-400 mt-0.5" />
                    <p className="text-sm text-yellow-800 dark:text-yellow-200">
                      Please select a specific station to add prices
                    </p>
                  </div>
                )}

                <div className="flex justify-end gap-2 pt-4">
                  <Button type="button" variant="outline" onClick={() => setPriceDialogOpen(false)}>
                    Cancel
                  </Button>
                  <Button type="submit" disabled={selectedStation === 'all'}>
                    Add Price
                  </Button>
                </div>
              </form>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="grid w-full max-w-md grid-cols-3">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="prices">Prices</TabsTrigger>
          <TabsTrigger value="inventory">Inventory</TabsTrigger>
        </TabsList>

        {/* Overview Tab */}
        <TabsContent value="overview" className="space-y-6">
          {/* Stats Cards */}
          <div className="grid gap-4 md:grid-cols-4">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Fuel Types</CardTitle>
                <Fuel className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{fuels.length}</div>
                <p className="text-xs text-muted-foreground">Available types</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Total Capacity</CardTitle>
                <Droplet className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{totalCapacity.toLocaleString()}</div>
                <p className="text-xs text-muted-foreground">Liters across all tanks</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Current Stock</CardTitle>
                <BarChart3 className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{totalStock.toLocaleString()}</div>
                <p className="text-xs text-muted-foreground">{stockPercentage.toFixed(1)}% capacity</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Active Prices</CardTitle>
                <DollarSign className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{activePricesCount}</div>
                <p className="text-xs text-muted-foreground">Currently effective</p>
              </CardContent>
            </Card>
          </div>

          {/* Fuel Type Details */}
          <FormCard
            title="Fuel Types & Statistics"
            description="Detailed breakdown by fuel type"
          >
            <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
              {fuels.map(fuel => {
                const stats = fuelStats.get(fuel.id)
                if (!stats) return null

                const stockPerc = stats.totalCapacity > 0
                  ? (stats.currentStock / stats.totalCapacity) * 100
                  : 0

                return (
                  <Card key={fuel.id} className="overflow-hidden">
                    <CardHeader className="pb-3 bg-muted/30">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-2">
                          <span className="text-2xl">{fuel.icon}</span>
                          <CardTitle className="text-sm font-medium">
                            {fuel.name}
                          </CardTitle>
                        </div>
                        <Badge variant="outline">{stats.tankCount} tanks</Badge>
                      </div>
                    </CardHeader>
                    <CardContent className="space-y-3 pt-4">
                      {/* Price */}
                      <div>
                        <p className="text-xs text-muted-foreground mb-1">Current Price</p>
                        <p className="text-2xl font-bold">
                          Rs. {stats.currentPrice.toFixed(2)}
                        </p>
                        <p className="text-xs text-muted-foreground">per liter</p>
                      </div>

                      {/* Inventory */}
                      <div className="pt-3 border-t space-y-1">
                        <div className="flex justify-between text-sm">
                          <span className="text-muted-foreground">Capacity:</span>
                          <span className="font-medium">{stats.totalCapacity.toLocaleString()} L</span>
                        </div>
                        <div className="flex justify-between text-sm">
                          <span className="text-muted-foreground">Stock:</span>
                          <span className="font-medium">{stats.currentStock.toLocaleString()} L</span>
                        </div>
                        <div className="flex justify-between text-sm">
                          <span className="text-muted-foreground">Fill Level:</span>
                          <span className={`font-medium ${stockPerc < 20 ? 'text-red-600' :
                            stockPerc < 50 ? 'text-yellow-600' :
                              'text-green-600'
                            }`}>
                            {stockPerc.toFixed(1)}%
                          </span>
                        </div>
                      </div>

                      {/* Progress Bar */}
                      <div className="w-full bg-muted rounded-full h-2">
                        <div
                          className={`h-2 rounded-full transition-all ${stockPerc < 20 ? 'bg-red-500' :
                            stockPerc < 50 ? 'bg-yellow-500' :
                              'bg-green-500'
                            }`}
                          style={{ width: `${Math.min(stockPerc, 100)}%` }}
                        />
                      </div>
                    </CardContent>
                  </Card>
                )
              })}
            </div>
          </FormCard>
        </TabsContent>

        {/* Prices Tab */}
        <TabsContent value="prices" className="space-y-6">
          {/* Current Prices */}
          <FormCard
            title="Current Prices"
            description="Latest effective prices for each fuel type"
          >
            {currentPrices.length > 0 ? (
              <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                {currentPrices.map(price => (
                  <Card key={price.id}>
                    <CardHeader className="pb-3">
                      <div className="flex items-center gap-2">
                        <span className="text-xl">{price.fuel?.icon || 'â›½'}</span>
                        <CardTitle className="text-sm font-medium text-muted-foreground">
                          {price.fuel?.name || 'Unknown Fuel'}
                        </CardTitle>
                      </div>
                    </CardHeader>
                    <CardContent>
                      <div className="text-3xl font-bold">
                        Rs. {price.price ? price.price.toFixed(2) : '0.00'}
                      </div>
                      <p className="text-xs text-muted-foreground mt-1">
                        per liter
                      </p>
                      <div className="mt-3 pt-3 border-t">
                        <p className="text-xs text-muted-foreground">
                          Effective: {price.effectiveDate ? new Date(price.effectiveDate).toLocaleDateString() : 'N/A'}
                        </p>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            ) : (
              <div className="text-center py-8 text-muted-foreground">
                No current prices available
              </div>
            )}
          </FormCard>

          {/* Price History */}
          <FormCard
            title="Price History"
            description={`All ${validPricesForHistory.length} price records (view only)`}
          >
            {validPricesForHistory.length > 0 ? (
              <DataTable
                data={validPricesForHistory}
                columns={columns}
                loading={loading}
              />
            ) : (
              <div className="text-center py-8 text-muted-foreground">
                {loading ? 'Loading...' : 'No price history available'}
              </div>
            )}
          </FormCard>
        </TabsContent>

        {/* Inventory Tab */}
        <TabsContent value="inventory" className="space-y-6">
          <FormCard
            title="Tank Inventory by Fuel Type"
            description={`Current stock levels across ${tanks.length} tanks`}
          >
            {tanks.length > 0 ? (
              <div className="space-y-6">
                {fuels.map(fuel => {
                  const fuelTanks = tanks.filter(t => t && t.fuelId === fuel.id)
                  const stats = fuelStats.get(fuel.id)

                  // Show all fuel types, even if no tanks
                  const totalCap = stats?.totalCapacity || 0
                  const currentStock = stats?.currentStock || 0
                  const fillPercentage = totalCap > 0 ? (currentStock / totalCap) * 100 : 0

                  return (
                    <div key={fuel.id} className="border rounded-lg p-4 space-y-3">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-2">
                          <span className="text-xl">{fuel.icon}</span>
                          <h3 className="font-semibold">{fuel.name}</h3>
                        </div>
                        <Badge variant={fuelTanks.length > 0 ? 'default' : 'secondary'}>
                          {fuelTanks.length} {fuelTanks.length === 1 ? 'tank' : 'tanks'}
                        </Badge>
                      </div>

                      {fuelTanks.length > 0 ? (
                        <>
                          <div className="grid gap-3 md:grid-cols-4 text-sm">
                            <div>
                              <p className="text-muted-foreground">Total Capacity</p>
                              <p className="text-lg font-semibold">{totalCap.toLocaleString()} L</p>
                            </div>
                            <div>
                              <p className="text-muted-foreground">Current Stock</p>
                              <p className="text-lg font-semibold">{currentStock.toLocaleString()} L</p>
                            </div>
                            <div>
                              <p className="text-muted-foreground">Available Space</p>
                              <p className="text-lg font-semibold">
                                {(totalCap - currentStock).toLocaleString()} L
                              </p>
                            </div>
                            <div>
                              <p className="text-muted-foreground">Fill Level</p>
                              <p className={`text-lg font-semibold ${fillPercentage < 20 ? 'text-red-600' :
                                fillPercentage < 50 ? 'text-yellow-600' :
                                  'text-green-600'
                                }`}>
                                {fillPercentage.toFixed(1)}%
                              </p>
                            </div>
                          </div>

                          {/* Progress Bar */}
                          <div className="w-full bg-muted rounded-full h-2">
                            <div
                              className={`h-2 rounded-full transition-all ${fillPercentage < 20 ? 'bg-red-500' :
                                fillPercentage < 50 ? 'bg-yellow-500' :
                                  'bg-green-500'
                                }`}
                              style={{ width: `${Math.min(fillPercentage, 100)}%` }}
                            />
                          </div>

                          {/* Individual Tank Details */}
                          <div className="pt-3 border-t">
                            <p className="text-sm font-medium text-muted-foreground mb-2">Individual Tanks:</p>
                            <div className="grid gap-2 md:grid-cols-2 lg:grid-cols-3">
                              {fuelTanks.map(tank => {
                                const tankFill = tank.capacity > 0 ? (tank.currentLevel / tank.capacity) * 100 : 0
                                return (
                                  <div key={tank.id} className="text-sm bg-muted/30 p-2 rounded">
                                    <div className="flex justify-between items-center mb-1">
                                      <span className="font-medium">{tank.tankNumber}</span>
                                      <span className="text-xs text-muted-foreground">
                                        {tank.station?.name || 'Unknown'}
                                      </span>
                                    </div>
                                    <div className="flex justify-between text-xs">
                                      <span>Stock: {tank.currentLevel.toLocaleString()} L</span>
                                      <span>Cap: {tank.capacity.toLocaleString()} L</span>
                                    </div>
                                    <div className="w-full bg-background rounded-full h-1 mt-1">
                                      <div
                                        className={`h-1 rounded-full ${tankFill < 20 ? 'bg-red-500' :
                                          tankFill < 50 ? 'bg-yellow-500' :
                                            'bg-green-500'
                                          }`}
                                        style={{ width: `${Math.min(tankFill, 100)}%` }}
                                      />
                                    </div>
                                  </div>
                                )
                              })}
                            </div>
                          </div>
                        </>
                      ) : (
                        <p className="text-sm text-muted-foreground py-2">No tanks configured for this fuel type</p>
                      )}
                    </div>
                  )
                })}
              </div>
            ) : (
              <div className="text-center py-8 text-muted-foreground">
                {loading ? 'Loading tank data...' : 'No tanks found'}
              </div>
            )}
          </FormCard>
        </TabsContent>
      </Tabs>
    </div>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'
import { Badge } from '@/components/ui/badge'
import { Loader2, User, Lock, Shield, ArrowLeft } from 'lucide-react'
import { useRouter } from 'next/navigation'

interface UserProfile {
  id: string
  username: string
  email: string
  role: string
  station_id?: string
  is_active: boolean
}

export default function ProfilePage() {
  const router = useRouter()
  const [profile, setProfile] = useState<UserProfile | null>(null)
  const [loading, setLoading] = useState(true)
  const [updating, setUpdating] = useState(false)
  const [error, setError] = useState('')
  const [success, setSuccess] = useState('')

  // Profile update form
  const [profileForm, setProfileForm] = useState({
    username: ''
  })

  // Password change form
  const [passwordForm, setPasswordForm] = useState({
    current_password: '',
    new_password: '',
    confirm_password: ''
  })

  useEffect(() => {
    fetchProfile()
  }, [])

  const fetchProfile = async () => {
    try {
      setLoading(true)
      setError('')

      // Get user data from localStorage first (fallback)
      const userId = localStorage.getItem('userId')
      const username = localStorage.getItem('username')
      const userRole = localStorage.getItem('userRole')
      const token = localStorage.getItem('accessToken')

      if (!token) {
        setError('Not logged in. Redirecting to login...')
        setTimeout(() => {
          window.location.href = '/login'
        }, 1500)
        setLoading(false)
        return
      }

      // Try to fetch from API
      const response = await fetch('/api/auth/me', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      })

      if (response.ok) {
        const data = await response.json()
        setProfile(data)
        setProfileForm({
          username: data.username
        })
      } else {
        // If API fails but we have localStorage data, use that as fallback
        if (userId && username && userRole) {
          console.warn('Using localStorage fallback for profile data')
          const fallbackProfile = {
            id: userId,
            username: username,
            email: '',
            role: userRole,
            station_id: localStorage.getItem('stationId') || undefined,
            is_active: true
          }
          setProfile(fallbackProfile)
          setProfileForm({
            username: username
          })
          setError('Could not verify session with server, using cached profile data')
        } else {
          const errorData = await response.json().catch(() => ({}))
          console.error('Profile fetch error:', response.status, errorData)
          setError(errorData.detail || errorData.error || `Failed to load profile (${response.status})`)
        }
      }
    } catch (error) {
      console.error('Profile fetch exception:', error)

      // Use localStorage fallback on network error
      const userId = localStorage.getItem('userId')
      const username = localStorage.getItem('username')
      const userRole = localStorage.getItem('userRole')

      if (userId && username && userRole) {
        const fallbackProfile = {
          id: userId,
          username: username,
          email: '',
          role: userRole,
          station_id: localStorage.getItem('stationId') || undefined,
          is_active: true
        }
        setProfile(fallbackProfile)
        setProfileForm({
          username: username
        })
        setError('Could not connect to server, using cached profile data')
      } else {
        setError('Unable to load profile. Please try logging in again.')
      }
    } finally {
      setLoading(false)
    }
  }

  const handleProfileUpdate = async (e: React.FormEvent) => {
    e.preventDefault()
    setUpdating(true)
    setError('')
    setSuccess('')

    try {
      const token = localStorage.getItem('accessToken')

      if (!token) {
        setError('No authentication token found. Please log in again.')
        setUpdating(false)
        return
      }

      const response = await fetch('/api/profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(profileForm)
      })

      if (response.ok) {
        const data = await response.json()
        setProfile(data)
        setSuccess('Profile updated successfully')
        // Update localStorage with new username
        localStorage.setItem('username', data.username)
      } else {
        const errorData = await response.json()
        setError(errorData.detail || 'Failed to update profile')
      }
    } catch (error) {
      setError('Unable to connect to server')
    } finally {
      setUpdating(false)
    }
  }

  const handlePasswordChange = async (e: React.FormEvent) => {
    e.preventDefault()
    setUpdating(true)
    setError('')
    setSuccess('')

    if (passwordForm.new_password !== passwordForm.confirm_password) {
      setError('New passwords do not match')
      setUpdating(false)
      return
    }

    if (passwordForm.new_password.length < 6) {
      setError('New password must be at least 6 characters')
      setUpdating(false)
      return
    }

    try {
      const token = localStorage.getItem('accessToken')

      if (!token) {
        setError('No authentication token found. Please log in again.')
        setUpdating(false)
        return
      }

      const response = await fetch('/api/profile/change-password', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          current_password: passwordForm.current_password,
          new_password: passwordForm.new_password
        })
      })

      if (response.ok) {
        setSuccess('Password updated successfully. Please log in again with your new password.')
        setPasswordForm({
          current_password: '',
          new_password: '',
          confirm_password: ''
        })
        // Clear the current session since password changed
        localStorage.removeItem('accessToken')
        localStorage.removeItem('userRole')
        localStorage.removeItem('userId')
        localStorage.removeItem('username')
        // Redirect to login after a short delay
        setTimeout(() => {
          window.location.href = '/login'
        }, 2000)
      } else {
        const errorData = await response.json()
        setError(errorData.detail || 'Failed to change password')
      }
    } catch (error) {
      setError('Unable to connect to server')
    } finally {
      setUpdating(false)
    }
  }

  const getRoleColor = (role: string) => {
    switch (role) {
      case 'OWNER': return 'bg-orange-500/20 text-orange-400 dark:bg-orange-600/30 dark:text-orange-300'
      case 'MANAGER': return 'bg-orange-500/20 text-orange-400 dark:bg-orange-600/30 dark:text-orange-300'
      case 'ACCOUNTS': return 'bg-green-500/20 text-green-400 dark:bg-green-600/30 dark:text-green-300'
      default: return 'bg-muted text-foreground'
    }
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <Loader2 className="h-8 w-8 animate-spin" />
      </div>
    )
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center gap-4">
        <Button variant="outline" onClick={() => router.push('/settings')}>
          <ArrowLeft className="mr-2 h-4 w-4" />
          Back
        </Button>
        <div>
          <h1 className="text-3xl font-bold text-foreground flex items-center gap-2">
            <User className="h-8 w-8 text-orange-600 dark:text-orange-400" />
            Profile Settings
          </h1>
          <p className="text-muted-foreground mt-1">
            Manage your account information and security settings
          </p>
        </div>
      </div>

      {/* Profile Information */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <User className="h-5 w-5" />
            Profile Information
          </CardTitle>
          <CardDescription>
            Your current account details and role information
          </CardDescription>
        </CardHeader>
        <CardContent>
          {profile && (
            <div className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label className="text-sm font-medium text-muted-foreground">Username</Label>
                  <p className="text-lg font-semibold">{profile.username}</p>
                </div>
                <div>
                  <Label className="text-sm font-medium text-muted-foreground">Role</Label>
                  <div className="mt-1">
                    <Badge className={getRoleColor(profile.role)}>
                      {profile.role}
                    </Badge>
                  </div>
                </div>
                <div>
                  <Label className="text-sm font-medium text-muted-foreground">Status</Label>
                  <div className="mt-1">
                    <Badge variant={profile.is_active ? 'default' : 'secondary'}>
                      {profile.is_active ? 'Active' : 'Inactive'}
                    </Badge>
                  </div>
                </div>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Settings Tabs */}
      <Tabs defaultValue="profile" className="space-y-6">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="profile" className="flex items-center gap-2">
            <User className="h-4 w-4" />
            Profile Settings
          </TabsTrigger>
          <TabsTrigger value="password" className="flex items-center gap-2">
            <Lock className="h-4 w-4" />
            Change Password
          </TabsTrigger>
        </TabsList>

        {/* Profile Settings Tab */}
        <TabsContent value="profile">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <User className="h-5 w-5" />
                Update Profile
              </CardTitle>
              <CardDescription>
                Update your username
              </CardDescription>
            </CardHeader>
            <CardContent>
              {error && (
                <Alert variant="destructive" className="mb-4">
                  <AlertDescription>{error}</AlertDescription>
                </Alert>
              )}
              {success && (
                <Alert className="mb-4 border-green-500/20 dark:border-green-500/30 bg-green-500/10 dark:bg-green-500/20">
                  <AlertDescription className="text-green-700 dark:text-green-300">{success}</AlertDescription>
                </Alert>
              )}

              <form onSubmit={handleProfileUpdate} className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="username">Username</Label>
                  <Input
                    id="username"
                    type="text"
                    value={profileForm.username}
                    onChange={(e) => setProfileForm({ ...profileForm, username: e.target.value })}
                    placeholder="Enter your username"
                    disabled={updating}
                    required
                  />
                </div>


                <Button type="submit" disabled={updating} className="w-full">
                  {updating ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      Updating...
                    </>
                  ) : (
                    'Update Profile'
                  )}
                </Button>
              </form>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Change Password Tab */}
        <TabsContent value="password">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Lock className="h-5 w-5" />
                Change Password
              </CardTitle>
              <CardDescription>
                Update your password for better security
              </CardDescription>
            </CardHeader>
            <CardContent>
              {error && (
                <Alert variant="destructive" className="mb-4">
                  <AlertDescription>{error}</AlertDescription>
                </Alert>
              )}
              {success && (
                <Alert className="mb-4 border-green-500/20 dark:border-green-500/30 bg-green-500/10 dark:bg-green-500/20">
                  <AlertDescription className="text-green-700 dark:text-green-300">{success}</AlertDescription>
                </Alert>
              )}

              <form onSubmit={handlePasswordChange} className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="current_password">Current Password</Label>
                  <Input
                    id="current_password"
                    type="password"
                    value={passwordForm.current_password}
                    onChange={(e) => setPasswordForm({ ...passwordForm, current_password: e.target.value })}
                    placeholder="Enter your current password"
                    disabled={updating}
                    required
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="new_password">New Password</Label>
                  <Input
                    id="new_password"
                    type="password"
                    value={passwordForm.new_password}
                    onChange={(e) => setPasswordForm({ ...passwordForm, new_password: e.target.value })}
                    placeholder="Enter your new password"
                    disabled={updating}
                    required
                    minLength={6}
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="confirm_password">Confirm New Password</Label>
                  <Input
                    id="confirm_password"
                    type="password"
                    value={passwordForm.confirm_password}
                    onChange={(e) => setPasswordForm({ ...passwordForm, confirm_password: e.target.value })}
                    placeholder="Confirm your new password"
                    disabled={updating}
                    required
                    minLength={6}
                  />
                </div>

                <Button type="submit" disabled={updating} className="w-full">
                  {updating ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      Updating...
                    </>
                  ) : (
                    'Change Password'
                  )}
                </Button>
              </form>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { FormCard } from '@/components/ui/FormCard'
import { DataTable } from '@/components/ui/DataTable'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Building2, Plus, Edit, Trash2, MapPin, Phone, Clock } from 'lucide-react'
import { useToast } from '@/hooks/use-toast'
import { useRouter } from 'next/navigation'
import { ArrowLeft } from 'lucide-react'

interface Station {
  id: string
  name: string
  address: string
  city: string
  phone?: string
  email?: string
  openingHours?: string
  isActive: boolean
  createdAt: string
  updatedAt: string
}

export default function StationsPage() {
  const router = useRouter()
  const [stations, setStations] = useState<Station[]>([])
  const [loading, setLoading] = useState(true)
  const [dialogOpen, setDialogOpen] = useState(false)
  const [editingStation, setEditingStation] = useState<Station | null>(null)
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    city: '',
    phone: '',
    email: '',
    openingHours: '',
    isActive: true
  })
  const { toast } = useToast()

  // Check if user is DEVELOPER (only role that can add/delete stations)
  const userRole = typeof window !== 'undefined' ? localStorage.getItem('userRole') : null
  const isDeveloper = userRole === 'DEVELOPER'

  useEffect(() => {
    fetchStations()
  }, [])

  const fetchStations = async () => {
    try {
      const response = await fetch('/api/stations')
      const data = await response.json()
      setStations(data)
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to fetch stations",
        variant: "destructive"
      })
    } finally {
      setLoading(false)
    }
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()

    try {
      const url = editingStation ? `/api/stations/${editingStation.id}` : '/api/stations'
      const method = editingStation ? 'PUT' : 'POST'

      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      })

      if (!response.ok) throw new Error('Failed to save station')

      toast({
        title: "Success",
        description: `Station ${editingStation ? 'updated' : 'created'} successfully`
      })

      setDialogOpen(false)
      resetForm()
      fetchStations()
    } catch (error) {
      toast({
        title: "Error",
        description: `Failed to ${editingStation ? 'update' : 'create'} station`,
        variant: "destructive"
      })
    }
  }

  const handleEdit = (station: Station) => {
    setEditingStation(station)
    setFormData({
      name: station.name,
      address: station.address,
      city: station.city,
      phone: station.phone || '',
      email: station.email || '',
      openingHours: station.openingHours || '',
      isActive: station.isActive
    })
    setDialogOpen(true)
  }

  const handleDelete = async (station: Station) => {
    if (!confirm(`Are you sure you want to delete station "${station.name}"?`)) return

    try {
      const response = await fetch(`/api/stations/${station.id}`, {
        method: 'DELETE'
      })

      if (!response.ok) throw new Error('Failed to delete station')

      toast({
        title: "Success",
        description: "Station deleted successfully"
      })

      fetchStations()
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete station",
        variant: "destructive"
      })
    }
  }

  const resetForm = () => {
    setEditingStation(null)
    setFormData({
      name: '',
      address: '',
      city: '',
      phone: '',
      email: '',
      openingHours: '',
      isActive: true
    })
  }

  const getStatusColor = (isActive: boolean) => {
    return isActive ? 'bg-green-500/20 text-green-400 dark:bg-green-600/30 dark:text-green-300' : 'bg-muted text-foreground'
  }

  const columns = [
    {
      key: 'name' as keyof Station,
      title: 'Station Name',
      render: (value: unknown) => (
        <div className="font-medium">{value as string}</div>
      )
    },
    {
      key: 'address' as keyof Station,
      title: 'Address',
      render: (value: unknown) => (
        <div className="flex items-center gap-1 text-sm text-muted-foreground">
          <MapPin className="h-3 w-3" />
          {value as string}
        </div>
      )
    },
    {
      key: 'city' as keyof Station,
      title: 'City',
      render: (value: unknown) => (
        <span className="text-sm">{value as string}</span>
      )
    },
    {
      key: 'phone' as keyof Station,
      title: 'Phone',
      render: (value: unknown) => (
        <div className="flex items-center gap-2">
          <Phone className="h-4 w-4 text-muted-foreground" />
          <span className="text-sm">{value ? (value as string) : '-'}</span>
        </div>
      )
    },
    {
      key: 'openingHours' as keyof Station,
      title: 'Opening Hours',
      render: (value: unknown) => (
        <div className="flex items-center gap-2">
          <Clock className="h-4 w-4 text-muted-foreground" />
          <span className="text-sm">{value ? (value as string) : '-'}</span>
        </div>
      )
    },
    {
      key: 'isActive' as keyof Station,
      title: 'Status',
      render: (value: unknown) => (
        <Badge className={getStatusColor(value as boolean)}>
          {(value as boolean) ? 'Active' : 'Inactive'}
        </Badge>
      )
    },
    {
      key: 'id' as keyof Station,
      title: 'Actions',
      render: (value: unknown, row: Station) => (
        <div className="flex items-center gap-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => handleEdit(row)}
          >
            <Edit className="h-4 w-4" />
          </Button>
          {/* Only DEVELOPER can delete stations */}
          {isDeveloper && (
            <Button
              variant="ghost"
              size="sm"
              onClick={() => handleDelete(row)}
              className="text-red-600 dark:text-red-400 hover:text-red-700"
            >
              <Trash2 className="h-4 w-4" />
            </Button>
          )}
        </div>
      )
    }
  ]

  const stationsList = Array.isArray(stations) ? stations : []

  const stats = [
    {
      title: 'Total Stations',
      value: stationsList.length.toString(),
      description: 'Registered stations',
      icon: <Building2 className="h-5 w-5 text-orange-600 dark:text-orange-400" />
    },
    {
      title: 'Active',
      value: stationsList.filter(s => s.isActive).length.toString(),
      description: 'Currently operational',
      icon: <div className="h-5 w-5 bg-green-500/10 dark:bg-green-500/200 rounded-full" />
    },
    {
      title: 'Inactive',
      value: stationsList.filter(s => !s.isActive).length.toString(),
      description: 'Not operational',
      icon: <div className="h-5 w-5 bg-red-500/10 dark:bg-red-500/200 rounded-full" />
    }
  ]

  return (
    <div className="space-y-6 p-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          <Button variant="outline" onClick={() => router.push('/settings')}>
            <ArrowLeft className="mr-2 h-4 w-4" />
            Back
          </Button>
          <div>
            <h1 className="text-3xl font-bold text-foreground">Station Management</h1>
            <p className="text-muted-foreground mt-2">
              Manage petrol stations, locations, and operational details
            </p>
          </div>
        </div>

        {/* Only DEVELOPER can see Add Station button */}
        {isDeveloper && (
          <Button onClick={() => { resetForm(); setDialogOpen(true); }}>
            <Plus className="mr-2 h-4 w-4" />
            Add Station
          </Button>
        )}
      </div>

      {/* Dialog for both Add and Edit - visible to all roles */}
      <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>
              {editingStation ? 'Edit Station' : 'Add New Station'}
            </DialogTitle>
          </DialogHeader>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <Label htmlFor="name">Station Name</Label>
              <Input
                id="name"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                placeholder="e.g., Main Street Station"
                required
              />
            </div>

            <div>
              <Label htmlFor="address">Address</Label>
              <Input
                id="address"
                value={formData.address}
                onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                placeholder="Full address"
                required
              />
            </div>

            <div>
              <Label htmlFor="city">City</Label>
              <Input
                id="city"
                value={formData.city}
                onChange={(e) => setFormData({ ...formData, city: e.target.value })}
                placeholder="City name"
                required
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="phone">Phone Number</Label>
                <Input
                  id="phone"
                  value={formData.phone}
                  onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                  placeholder="e.g., +1 234 567 8900"
                />
              </div>
              <div>
                <Label htmlFor="email">Email</Label>
                <Input
                  id="email"
                  type="email"
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  placeholder="station@example.com"
                />
              </div>
            </div>

            <div>
              <Label htmlFor="openingHours">Opening Hours</Label>
              <Input
                id="openingHours"
                value={formData.openingHours}
                onChange={(e) => setFormData({ ...formData, openingHours: e.target.value })}
                placeholder="e.g., Mon-Fri: 6AM-10PM, Sat-Sun: 7AM-9PM"
              />
            </div>

            <div>
              <Label htmlFor="isActive">Status</Label>
              <Select
                value={formData.isActive ? 'active' : 'inactive'}
                onValueChange={(value) => setFormData({ ...formData, isActive: value === 'active' })}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="active">Active</SelectItem>
                  <SelectItem value="inactive">Inactive</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="flex justify-end gap-3 pt-4">
              <Button type="button" variant="outline" onClick={() => setDialogOpen(false)}>
                Cancel
              </Button>
              <Button type="submit">
                {editingStation ? 'Update Station' : 'Create Station'}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {stats.map((stat, index) => (
          <Card key={index}>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <div className="text-2xl font-bold text-foreground">{stat.value}</div>
                  <div className="text-sm font-medium text-foreground">{stat.title}</div>
                  <div className="text-xs text-muted-foreground">{stat.description}</div>
                </div>
                <div className="flex-shrink-0">
                  {stat.icon}
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Stations Table */}
      <FormCard title="Stations" description="Manage all petrol station locations and details">
        <DataTable
          data={stations}
          columns={columns}
          searchPlaceholder="Search stations..."
          enableExport={true}
          exportFileName="stations-list"
        />
      </FormCard>
    </div>
  )
}
